body:
{% schema %}
{
  "name": "ブログ記事一覧",
  "class": "blogposts--section",
  "settings": [
    {
      "type": "text",
      "id": "title",
      "label": "見出し",
      "default": "ブログ記事一覧"
    },
    {
      "type": "blog",
      "id": "blog",
      "label": "ブログの選択"
    },
    {
      "type": "range",
      "id": "end_count",
      "label": "表示する記事数",
      "min": 0,
      "max": 10,
      "step": 1,
      "default": 4
    },
    {
      "type": "checkbox",
      "id": "show_date",
      "label": "投稿日を表示",
      "default": true
    },
    {
      "type": "range",
      "id": "pc_margin_top",
      "label": "PC上マージン",
      "min": 0,
      "max": 200,
      "step": 10,
      "unit": "px",
      "default": 0
    },
    {
      "type": "range",
      "id": "pc_margin_bottom",
      "label": "PC下マージン",
      "min": 0,
      "max": 200,
      "step": 10,
      "unit": "px",
      "default": 0
    },
    {
      "type": "range",
      "id": "sp_margin_top",
      "label": "SP上マージン",
      "min": 0,
      "max": 200,
      "step": 10,
      "unit": "px",
      "default": 0
    },
    {
      "type": "range",
      "id": "sp_margin_bottom",
      "label": "SP下マージン",
      "min": 0,
      "max": 200,
      "step": 10,
      "unit": "px",
      "default": 0
    }
  ],
  "max_blocks": 4,
  "blocks": [
    {
      "type": "article",
      "name": "article",
      "settings": [
        {
          "id": "article",
          "type": "article",
          "label": "article"
        }
      ]
    }
  ],
  "presets": [
    {
      "category": "Blog",
      "name": "ブログ記事一覧",
      "settings": {
        "blog": "none"
      }
    }
  ]
}

{% endschema %}
